import 'dart:scalarlist';

void dsa_test_vectors(Dsa dsa) {
  String message0 ="message";
  String message1 ="test";
  String message2 ="";
  String message3 ="A message which is longer than a short one and hence might trigger bugs due to long messages which are long long long long................. _* This is a long message *_ ..........";
  BigInteger privateKey0 = new BigInteger(  );
  BigInteger publicKey0 = new BigInteger(  );
  BigInteger privateKey1 = new BigInteger(  );
  BigInteger publicKey1 = new BigInteger(  );
  BigInteger privateKey2 = new BigInteger(  );
  BigInteger publicKey2 = new BigInteger(  );
  BigInteger privateKey3 = new BigInteger(  );
  BigInteger publicKey3 = new BigInteger(  );
  BigInteger privateKey4 = new BigInteger(  );
  BigInteger publicKey4 = new BigInteger(  );
  List<DSATestVector> testVectors = [ new DSATestVector(privateKey0,publicKey0,message0,new BigInteger( 0x727b40c7e63510addb70142f28c43ae061132055a8d500115cdf4ee7fe5572c0 ),new BigInteger( 0x7abd1856e5a568ea665a78f90743bf8642a687d0085c6ac0d50e7cd07c5befd2 )),
  new DSATestVector(privateKey0,publicKey0,message1,new BigInteger( 0x008a257fde3b08ba4114bc881d822416f36dbe59d1d2ab9351df8f39ba0d3a112a ),new BigInteger( 0x00962ca7d37cf8281747fd480d047b85c0af608fdfdff8bc028f179ea0c02c3bda )),
  new DSATestVector(privateKey0,publicKey0,message2,new BigInteger( 0x00989198cddbaaacc048e1791dc17026c39b9878a3a17c45b1ebb2d6a92ac14cb0 ),new BigInteger( 0x13d460e95a2869f23fd0dc723f0f590136dc60d2ca5b62624bac06bd9a1e9043 )),
  new DSATestVector(privateKey0,publicKey0,message3,new BigInteger( 0x2e6472861fbaf1190c8fac6863bdde2141d692696c63943b1b76e2587ca04bf8 ),new BigInteger( 0x7e4fdd3ae5f01658f28b360bd73cd0c583acd8cd9f9b9508c6f4ab68809464e9 )),
  new DSATestVector(privateKey1,publicKey1,message0,new BigInteger( 0x1ccc8a69846fe99d0612b60d2db854d66949dda5e7a2169fbb2980e1333b8123 ),new BigInteger( 0x76e1c7604d43b5f94654bdc85e2b6efbc304a69f2b124d1ede372cd76237de7e )),
  new DSATestVector(privateKey1,publicKey1,message1,new BigInteger( 0x00a8658b22b53c8a78d862742596a3f35874b3798736354f26d7007f5d6c757778 ),new BigInteger( 0x68e314fef51eea7a00ab81a7e3b0f52b261a9ba847e4596793a84598110590c9 )),
  new DSATestVector(privateKey1,publicKey1,message2,new BigInteger( 0x21d46f9f37009b15ed622708cebb6a18a55d4adf501d150e4cfd0eca94ea9d2b ),new BigInteger( 0x28621902f1481980f63b518ce1a4f65e32e61efa37464133c57a28ff974b411e )),
  new DSATestVector(privateKey1,publicKey1,message3,new BigInteger( 0x00b982af22ca1e864ee7ea6d8fb6f72b14b1bd6f78071c3a1c61211980634d175d ),new BigInteger( 0x7b8d086e18fc9d0daff704326918668894d31209981da5584c73e5ba271b0799 )),
  new DSATestVector(privateKey2,publicKey2,message0,new BigInteger( 0x1b876095652a552730b038c52469a88db90197c99ea00b7b7ec7662ce24ba341 ),new BigInteger( 0x1961e2d3a35b429c6bdaa27db81ebf2ad517a164b9c27b2d4875c0f710dcee6b )),
  new DSATestVector(privateKey3,publicKey3,message0,new BigInteger( 0x00ac72b384fd715814417d3b344d2e9962f91e4d03a9627213d12cf9083e6f26e2 ),new BigInteger( 0x44e0a2827f4ead2d2057ec26304595bb658db95cfaffedba57002f7836aaa698 )),
  new DSATestVector(privateKey4,publicKey4,message0,new BigInteger( 0x52edf7c3b0f8a66e765724254a0bdbd5bb4651e2757db0121e284e2f6cf8a7ca ),new BigInteger( 0x17214f43286e3ca34186866064a0f84f59a845218d8a838eae11b9868edf0508 ))
                               ];
  int i = 0;
  testVectors.forEach((vector) =>
    test('dsa verifies ${i++}', () => dsa.verify(toBytes(vector.message),new DsaSignature(vector.r,vector.s),vector.publicKey))
  );
}

class DSATestVector {
  final BigInteger privateKey;
  final BigInteger publicKey;
  final String message;
  final BigInteger r;
  final BigInteger s;
  DSATestVector(this.privateKey,this.publicKey,this.message,this.r,this.s);
}

void main() {
  Dsa dsa = new Dsa(Dsa.parameters3072);
  test('dsa sign runs', () => dsa.sign(toBytes("message"),new BigInteger("fff")));
  test('dsa generateInsecureKeypair runs', () => dsa.generateInsecureKeyPair());
  DsaKeyPair keyPair = dsa.generateInsecureKeyPair();
  ByteArray message = toBytes("message");
  DsaSignature signature = dsa.sign(message, keyPair.privateKey);

  test('dsa sign with key verifies', () => dsa.verify(message, signature, keyPair.publicKey));
  dsa_test_vectors(dsa);
}